sources.css/index.scss:
$dark-gray: 		#20282f;
$light-blue: 		#83AADB;
$react-bg: 			#222;
$react-color:		#32BBF0;
/* $react-color:		#61dafb;*/
$firebase-bg: 		#32BBF0;
/* $firebase-bg: 		#039be5;*/
$firebase-front: 	#ffcb2c;
$firebase-mid:		#f5820c;
$firebase-back:		#ffa50d;
$three-bg:			#FFB81D;
$smooth-transition: cubic-bezier(0.25, 0, .1, 1);

@font-face {
  font-family: 'icomoon';
  src:  url('fonts/icomoon.eot');
  src:  url('fonts/icomoon.eot#iefix') format('embedded-opentype'),
    url('fonts/icomoon.ttf') format('truetype'),
    url('fonts/icomoon.woff') format('woff'),
    url('fonts/icomoon.svg#icomoon') format('svg');
  font-weight: normal;
  font-style: normal;
}

[class^="icon-"], [class*=" icon-"] {
  /* use !important to prevent issues with browser extensions that change fonts */
  font-family: 'icomoon' !important;
  speak: none;
  font-style: normal;
  font-weight: normal;
  font-variant: normal;
  text-transform: none;
  line-height: 1;

  /* Better Font Rendering =========== */
  -webkit-font-smoothing: antialiased;
  -moz-osx-font-smoothing: grayscale;
}

.icon-chevron-thin-down:before {
  content: "\e900";
}
.icon-cross:before {
  content: "\ea0f";
}
.icon-new-tab:before {
  content: "\ea7e";
}
.icon-mail4:before {
  content: "\ea86";
}
.icon-github:before {
  content: "\eab0";
}
.icon-linkedin:before {
  content: "\eac9";
}
.icon-stackoverflow:before {
  content: "\ead0";
}

* {
	box-sizing: border-box;
	-webkit-overflow-scrolling: touch;
}

body {
	font-family: "Open Sans", sans-serif;
	color: $dark-gray;
	&.overlay-active {
	}
}

img {
	display: block !important;
	padding: 0 !important;
	border: none !important;
	border-radius: 0 !important;
}

a {
	transition: .15s all cubic-bezier(0.25, 0, .1, 1);
	color: $dark-gray;
	text-decoration: underline;
	&:hover {
		color: white;
		background: $dark-gray;
	}
}

p {
	line-height: 1.6;
}

section {
	padding: 50px 0;
	margin-top: 50px;
}

footer {
	padding: 50px 0;
}

#main {
	width: 100%;
	height: 100%;
	overflow: hidden;
	position: relative;
}

#nav {
	position: fixed;
	z-index: 1000;
	top: 0;
	left: 0;
	background: white;
	width: 100%;
	font-family: "Inconsolata", monospace;

	a {
		line-height: 40px;
		font-size: 18px;

	}

	#to-home {
		float: left;
	}

	#right-group {
		float: right;
	}

	.nav-button {
		display: inline-block;
	}
}

#hero {
	width: 100vw;
	height: 100vh;
	min-height: 560px;
	background: $dark-gray;
	color: white;
	position: relative;

	#hero-floater {
		position: absolute;
		left: 50%;
		top: 45%;
		color: white;
		transform: translate(-50%, -50%);
		display: inline-block;
		white-space: nowrap;
		text-align: center;

