$ratio-sm: 768 / 150;
$ratio-md: 992 / 150;
$ratio-lg: 1200 / 150;

@mixin calc-ratio($prop, $num){
	@media screen and (min-width: 768px){
		#{$prop}: $num * $ratio-sm;
	}
	@media screen and (min-width: 992px){
		#{$prop}: $num * $ratio-md;
	}
	@media screen and (min-width: 1200px){
		#{$prop}: $num * $ratio-lg;
	}
}

@mixin calc-ratio-transform-y($x, $y){
	@media screen and (min-width: 768px){
		transform: translate($x, $y * $ratio-sm);
	}
	@media screen and (min-width: 992px){
		transform: translate($x, $y * $ratio-md);
	}
	@media screen and (min-width: 1200px){
		transform: translate($x, $y * $ratio-lg);
	}
}

@function calc-sm($a){
	@return $a * $ratio-sm;
}
@function calc-md($a){
	@return $a * $ratio-md;
}
@function calc-lg($a){
	@return $a * $ratio-lg;
}

@mixin query-sm($prop, $op){
	@media screen and (min-width: 768px){
		#{$prop}: $op;
	}
}
@mixin query-md($prop, $op){
	@media screen and (min-width: 992px){
		#{$prop}: $op;
	}
}
@mixin query-lg($prop, $op){
	@media screen and (min-width: 1200px){
		#{$prop}: $op;
	}
}

#obsessions {
	#gadget-wrapper {
		display: -ms-flexbox;
		display: flex;
		.gadget-box {
			-ms-flex-positive: 1;
			    flex-grow: 1;
			position: relative;
			overflow: hidden;

			&:hover, html.touchevents & {
				.gadget-name, .gadget-description {
					opacity: 1;
					transform: translate(-50%, -50%);
				}
			}
		}
		* {
			transition: all .3s cubic-bezier(0.215, 0.610, 0.355, 1.000);
		}

		@media screen and (max-width: 767px){
			& {
				-ms-flex-wrap: wrap;
				    flex-wrap: wrap;
				.gadget-box {
					width: 100%;
				}
			}
		}

		.gadget-name, .gadget-description {
			position: absolute;
			transform: translate(-50%, -45%);
			color: white;
			left: 50%;
			z-index: 10;
			text-align: center;
			width: calc(60vw / 3);
			font-family: "Inconsolata", monospace;
			pointer-events: none;
			opacity: 0;
			@media screen and (max-width: 767px){
				width: 60vw;
			}
			@include calc-ratio(width, 40px);
		}

